<template>
  <v-card>
    <v-row justify="space-between">
      <v-col>
        <v-treeview
          v-model="tree"
          activatable
          :active.sync="active"
          hoverable
          item-key="path"
          :items="treeItems"
          :open="treeItemsOpen"
          open-on-click
        >
          <template
            slot="prepend"
            slot-scope="{ item, open }"
          >
            <v-icon
              v-if="!item.file"
              color="blue"
            >
              {{ open ? 'mdi-folder-open' : 'mdi-folder' }}
            </v-icon>

            <img
              v-else-if="
                item.file === 'image/jpeg' || item.file === 'image/png'
              "
              class="directory-item"
              :src="item.url"
            >

            <img
              v-else-if="item.name.endsWith('.pdf')"
              class="directory-item"
              src="data:image/png;base64,"
            >

            <img
              v-else-if="item.name.endsWith('.docx')"
              class="directory-item"
              src="data:image/png;base64,"
            >

            <img
              v-else-if="item.name.endsWith('.xlsx')"
              class="directory-item"
              src="data:image/png;base64,"
            >

            <img
              v-else-if="item.name.endsWith('.xls')"
              class="directory-item"
              src="data:image/png;base64,"
            >

            <img
              v-else-if="item.name.endsWith('.pptx')"
              class="directory-item"
              src="data:image/png;base64,"
            >

            <v-icon v-else>
              mdi-text-box-outline
            </v-icon>
          </template>

          <template
            slot="append"
            slot-scope="{ item, open }"
          >
            <template v-if="!item.file">
              <v-btn
                id="step13"
                icon
                small
                @click.native.stop="openCreate(item)"
              >
                <v-icon
                  color="blue"
                  small
                >
                  mdi-folder-plus
                </v-icon>
              </v-btn>

              <v-btn
                id="step12"
                icon
                small
                @click.native.stop="selectFile(item)"
              >
                <v-icon
                  color="blue"
                  small
                >
                  mdi-plus
                </v-icon>
              </v-btn>

              <v-btn
                v-if="item.can_subfolder === true"
                icon
                small
                @click.stop="deleteFolder(item, open)"
              >
                <v-icon
                  color="red"
                  small
                >
                  mdi-delete
                </v-icon>
              </v-btn>
            </template>
            <template
              v-else-if="shareMode"
            >
              <v-btn
                icon
                small
                @click.stop="selectItemToShare(item)"
              >
                <v-icon small>
                  {{ (fileSelected.includes(item)) ? 'mdi-check-box-outline' : 'checkbox-blank-outline' }}
                </v-icon>
              </v-btn>
              <v-btn
                icon
                small
                @click.stop="GoToShare"
              >
                <v-icon small>
                  mdi-check-outline
                </v-icon>
              </v-btn>
            </template>

            <template v-else>
              <v-btn
                id="step14"
                :href="item.url"
                icon
                small
                target="_blank"
                @click.stop=""
              >
                <v-icon small>
                  mdi-folder-plus
                </v-icon>
              </v-btn>

              <v-btn
                id="step15"
                icon
                small
                @click.stop="GoshareMode(item)"
              >
                <v-icon small>
                  mdi-share
                </v-icon>
              </v-btn>

              <v-btn
                v-if="!item.readonly"
                icon
                small
                @click.stop="deleteFile(item, open)"
              >
                <v-icon
                  color="red"
                  small
                >
                  mdi-delete
                </v-icon>
              </v-btn>
            </template>
          </template>
        </v-treeview>
      </v-col>
    </v-row>

    <input
      v-show="false"
      id="files"
      ref="uploadInput"
      :multiple="true"
      name="file"
      type="file"
      @change="detectFiles($event)"
    >

    <v-dialog
      v-model="dialog"
      persistent
      width="300"
    >
      <v-card
        color="blue"
        dark
      >
        <v-card-text class="pt-4">
          {{ $t('general.uploadingFilesMessage') }}
          <div class="align-center d-flex justify-center my-1">
            <v-divider class="grey lighten-1" />
            <p class="grey--text mb-0 mx-2 text--lighten-1">
              {{ uploadStatusText }}
            </p>
            <v-divider class="grey lighten-1" />
          </div>
          <v-progress-linear
            class="mb-0"
            color="white"
            indeterminate
          />
        </v-card-text>
      </v-card>
    </v-dialog>

    <share-modal
      :item="fileSelected"
      :show="showShareModal"
      @close="closeShareModal"
    />

    <v-dialog
      v-model="modalNewFolder"
      persistent
      width="600px"
    >
      <v-card
        class="rounded-lg"
        flat
      >
        <v-card-title class="headline px-6 py-4 white">
          <span class="grey--text text--darken-1">{{ $t('cpm.projects.createNewFolder') }}</span>
        </v-card-title>
        <v-divider class="grey lighten-3" />

        <v-card-text class="vertical-scroll">
          <v-container
            class="fill-height ma-0 pa-0 rounded-lg"
            fluid
          >
            <v-text-field
              ref="folderName"
              v-model="folderName"
              label="Folder Name*"
              maxlength="40"
              required
            />
          </v-container>

          <small>{{ $t('general.requiredFields') }}</small>
        </v-card-text>

        <v-card-actions>
          <div class="flex-grow-1" />

          <v-btn
            color="blue darken-1"
            text
            @click="modalNewFolder = false"
          >
            {{ $t('general.close') }}
          </v-btn>

          <v-btn
            color="blue darken-1"
            text
            @click="createSubFolder"
          >
            {{ $t('general.create') }}
          </v-btn>
        </v-card-actions>
      </v-card>
    </v-dialog>

    <v-dialog
      :scrollable="Boolean(imageUrl)"
      :value="imageUrl || pdfUrl"
      width="90%"
    >
      <v-card>
        <v-card-title>
          <v-spacer />

          <v-btn
            icon
            @click="
              pdfUrl = ''
              imageUrl = ''
            "
          >
            <v-icon>
              mdi-close
            </v-icon>
          </v-btn>
        </v-card-title>

        <v-card-text class="vertical-scroll">
          <v-img
            v-if="imageUrl"
            contain
            :lazy-src="imageUrl"
            :src="imageUrl"
          >
            <v-row
              slot="placeholder"
              align="center"
              class="fill-height ma-0"
              justify="center"
            >
              <v-progress-circular
                color="blue lighten-2"
                indeterminate
              />
            </v-row>
          </v-img>

          <iframe
            v-if="pdfUrl"
            class="w-full"
            :src="pdfUrl"
            style="height: 750px; border: none"
          />
        </v-card-text>
      </v-card>
    </v-dialog>
  </v-card>
</template>

<script>
import { db, storage } from '@/utils/Firebase'
import ShareModal from './ShareModal'
import EventBus from '@/Eventbus'
import { defaultFilesStructure } from './DefaultFilesStructure'
import { mapState } from 'vuex'

export default {
  name: 'FileSystem',
  components: {
    'share-modal': ShareModal
  },
  data: () => ({
    folderName: '',
    imageUrl: '',
    pdfUrl: '',
    currentItem: {},
    tree: [],
    active: [],
    project: {
      files: []
    },
    treeItemsOpen: [],
    showingTour: false,
    dialog: false,
    firstLoad: true,
    modalNewFolder: false,
    showShareModal: false,
    uploadStatusText: '',
    shareMode: false,
    fileSelected: []
  }),

  computed: {
    ...mapState('Companies', {
      currentCompany: 'currentCompany'
    }),
    treeItems() {
      return this.showingTour
        ? [
          {
            name: 'Demo A',
            path: '/demo-a',
            children: [
              {
                name: 'Demo A-1',
                path: '/demo-a-1',
                children: [
                  { name: 'Demo A-1-1', file: 'application/pdf', url: '#' }
                ]
              }
            ]
          }
        ]
        : this.$h.dg(this.project, 'files.children', this.project.files || [])
    }
  },

  watch: {
    active() {
      if (!this.active.length) return undefined

      let data = null
      this.project.files.some((file, index, arr) => {
        data = this.searchFiles(file, this.active[0], arr[index])
        if (data) {
          return true
        }
      })

      if (data) {
        if (data.item.file === 'image/png' || data.item.file === 'image/jpeg') {
          this.imageUrl = data.item.url
          this.pdfUrl = null
        } else if (data.item.file === 'application/pdf') {
          this.pdfUrl = data.item.url
          this.imageUrl = null
        } else {
          this.imageUrl = null
          this.pdfUrl = null
        }
      }
    }
  },

  created() {
    EventBus.$on('projects-files-tour-show-up', show => {
      this.showingTour = show

      if (show) {
        this.treeItemsOpen = ['/demo-a', '/demo-a-1']
      } else {
        this.treeItemsOpen = []
      }
    })

    EventBus.$on('projects-files-tour-open-share-modal', show => {
      if (show) {
        this.share({ name: 'Demo A-1-1' })
      } else {
        this.showShareModal = false
        this.fileSelected = []
      }
    })
  },

  mounted() {
    this.initData()

    EventBus.$on('project-files-updated', () => {
      this.initData()
    })
  },

  methods: {
    initData() {
      this.getProject().then(async () => {
        await this.getPolicyAndProceduresFiles()

        if (!this.$h.dg(this.project, 'files', []).length) {
          this.$set(this.project, 'files', defaultFilesStructure)
          db.collection('cpm_projects')
            .doc(this.$route.params.id)
            .update({
              files: defaultFilesStructure
            })
            .then(() => {
              this.getPolicyAndProceduresFiles()
            })
        }
      })
    },
    GoshareMode(item) {
      this.shareMode = true
      this.selectItemToShare(item)
    },
    GoToShare() {
      this.showShareModal = true
      this.shareMode = false
    },
    closeShareModal() {
      this.showShareModal = false
      this.fileSelected = []
    },
    selectItemToShare(item) {
      if (!this.fileSelected.includes(item)) {
        this.fileSelected.push(item)
      } else {
        const index = this.fileSelected.indexOf(item)
        this.fileSelected.splice(index, 1)
      }
    },
    selectFile(item) {
      this.currentItem = item
      this.$refs.uploadInput.click()
    },

    openCreate(item) {
      this.modalNewFolder = true
      this.currentItem = item
    },

    createSubFolder() {
      this.modalNewFolder = false
      this.dialog = true
      const name = this.folderName
      const file_name = name.replace(/\s+/g, '_').toLowerCase()
      const duplicate = this.currentItem.children.find(
        c => c.name === name && !c.file
      )
      if (duplicate) {
        this.$snotify.error(this.$t('general.folderAlreadyExist'), 'Error')
        this.modalNewFolder = true
        this.$refs.folderName.focus()
      } else {
        this.currentItem.children.push({
          children: [],
          can_subfolder: true,
          name, // folder name
          path: this.currentItem.path + '/' + file_name
        })

        db.collection('cpm_projects')
          .doc(this.$route.params.id)
          .update({
            files: this.project.files
          })
      }

      this.dialog = false
    },

    async detectFiles(e) {
      const fileList = e.target.files || e.dataTransfer.files
      const duplicates = []

      Array.from(Array(fileList.length).keys()).map(x => {
        const file = fileList[x]
        const duplicate = this.currentItem.children.find(
          c => c.name === file.name
        )
        if (duplicate) {
          duplicates.push(duplicate)
        }
      })

      if (duplicates.length) {
        let msg = this.$t('general.fileOverwritten')
        duplicates.map(d => {
          msg += `${d.name}, `
        })
        msg += this.$t('general.youWantContinue')
        const confirmation = confirm(msg)
        if (confirmation) {
          duplicates.map(d => {
            const index = this.currentItem.children.indexOf(d)
            this.currentItem.children.splice(index, 1)
          })
        } else {
          return false
        }
      }

      this.dialog = true
      await Promise.all(
        Array.from(Array(fileList.length).keys()).map(async x => {
          const file = fileList[x]
          const filePath = `projects/${this.project.id}${this.currentItem.path}/${file.name}`

          this.uploadStatusText = this.$t('general.uploadIsStarting')

          const uploadTask = storage.ref(filePath).put(file)

          await uploadTask.on(
            'state_changed',
            snapshot => {
              // Get task progress, including the number of bytes uploaded and the total number of bytes to be uploaded
              const progress =
                (snapshot.bytesTransferred / snapshot.totalBytes) * 100
              this.uploadStatusText = this.$t('general.uploadPercentage', {
                percentage: progress.toFixed(0)
              })
              switch (snapshot.state) {
                case 'paused':
                  this.uploadStatusText = this.$t('general.uploadIsPause')
                  break
                case 'running':
                  //
                  break
              }
            },
            function (error) {
              // A full list of error codes is available at
              // https://firebase.google.com/docs/storage/web/handle-errors
              switch (error.code) {
                case 'storage/unauthorized':
                  // User doesn't have permission to access the object
                  break

                case 'storage/canceled':
                  // User canceled the upload
                  break

                case 'storage/unknown':
                  // Unknown error occurred, inspect error.serverResponse
                  break
              }
            },
            function () {
              return 1
              // Upload completed successfully
            }
          )

          return uploadTask
        })
      ).then(async uploadedFiles => {
        await Promise.all(
          uploadedFiles.map(snapshot =>
            snapshot.ref.getDownloadURL().then(downloadUrl => {
              this.currentItem.children.push({
                name: snapshot.ref.name,
                file: snapshot.metadata.contentType,
                size: snapshot.metadata.size,
                url: downloadUrl,
                path: snapshot.ref.fullPath
              })
            })
          )
        )
      })

      db.collection('cpm_projects')
        .doc(this.$route.params.id)
        .update({
          files: this.project.files
        })

      this.dialog = false
    },

    async deleteFolder(item) {
      let msg = this.$t('general.folderDelete', { folder: item.name })
      msg += '\n'
      msg += this.$t('general.youWantContinue')
      const confirmation = confirm(msg)
      if (confirmation) {
        this.dialog = true
        let data = null
        this.project.files.some((file, index, arr) => {
          data = this.searchFiles(file, item.path, arr[index])
          if (data) {
            return true
          }
        })
        const idx = data.parent.children.indexOf(data.item)
        data.parent.children.splice(idx, 1)
        const fileList = this.fileList(item)

        await Promise.all(
          fileList.map(x => {
            const filePath = x.path
            return storage.ref(filePath).delete()
          })
        )
        db.collection('cpm_projects')
          .doc(this.$route.params.id)
          .update({
            files: this.project.files
          })
        this.dialog = false
      }
    },

    fileList(list) {
      const fileList = []
      list.children.map(item => {
        if (item.file) fileList.push(item)
        if (item.children) {
          Array.prototype.push.apply(fileList, this.fileList(item))
        }
      })
      return fileList
    },

    deleteFile(item) {
      const confirmation = confirm(
        this.$t('general.fileToDelete', { file: item.name })
      )
      if (confirmation) {
        if (this.active.length && item.name === this.active[0]) {
          this.imageUrl = null
        }

        const deletePath = item.path
        let data = null
        this.project.files.some((file, index, arr) => {
          data = this.searchFiles(file, item.path, arr[index])
          if (data) {
            return true
          }
        })
        const idx = data.parent.children.indexOf(data.item)
        data.parent.children.splice(idx, 1)
        storage
          .ref(deletePath)
          .delete()
          .then(() => {
            db.collection('cpm_projects')
              .doc(this.$route.params.id)
              .update({
                files: this.project.files
              })
            this.$snotify.success(this.$t('general.fileDelete'), 'Success')
          })
          .catch(() => {})
      }
    },

    searchFiles(item, path, parent) {
      if (item.path === path) {
        return {
          item,
          parent
        }
      } else {
        if (item.children && item.children.length > 0) {
          let tempItem = null
          item.children.some(i => {
            tempItem = this.searchFiles(i, path, item)
            // break loop if item found
            if (tempItem) {
              return true
            }
          })

          return tempItem
        }
      }
    },

    findRecursiveParentFolder(itemParent, item) {
      if (!this.$h.dg(itemParent, 'children', []).length || itemParent.file) {
        return {
          parent: itemParent,
          indexInParent: -1
        }
      }

      const index = itemParent.children.findIndex(
        auxItem => item.path === auxItem.path
      )

      if (index < 0) {
        let aux

        for (let i = 0; i < itemParent.children.length; i++) {
          const element = itemParent.children[i]

          aux = this.findRecursiveParentFolder(element, item)

          if (aux.indexInParent > -1) {
            return aux
          }
        }
      }

      return {
        parent: itemParent,
        indexInParent: index
      }
    },

    getPolicyAndProceduresFiles() {
      const self = this
      return new Promise((resolve, reject) => {
        try {
          db.collection('m6companies')
            .doc(self.currentCompany.id)
            .collection('cpm')
            .doc('default_folders')
            .collection('policy_procedures')
            .get()
            .then(snap => {
              if (snap.empty) {
                resolve(true)
                return
              }
              // Not adding what is inside Bidding folder in project information because may have duplicate paths
              const rfps = this.$h.dg(
                this.project,
                'files.0.children.0.children',
                []
              )
              if (this.$h.dg(this.project, 'files.0.children.0.children')) {
                this.project.files[0].children[0].children = []
              }

              const files = {
                children: this.project.files,
                path: ''
              }

              let auxFolder = {}

              snap.docs.forEach(doc => {
                const aux = doc.data()
                aux.id = doc.id

                auxFolder = this.findRecursiveParentFolder(files, aux)

                if (auxFolder.indexInParent > -1 && aux.files) {
                  aux.files.forEach(file => {
                    file.readonly = true
                    const index = this.lodash.findIndex(
                      auxFolder.parent.children[auxFolder.indexInParent]
                        .children,
                      { name: this.$h.dg(file, 'name', '') }
                    )
                    if (index === -1) {
                      auxFolder.parent.children[
                        auxFolder.indexInParent
                      ].children.push(file)
                    } else {
                      auxFolder.parent.children[
                        auxFolder.indexInParent
                      ].children[index] = file
                    }
                  })
                }
              })
              // Restoring the folder
              rfps.forEach(rfp => {
                if (this.$h.dg(this.project, 'files.0.children.0.children')) {
                  const index = this.lodash.findIndex(
                    this.project.files[0].children[0].children,
                    {
                      name: this.$h.dg(rfp, 'name', '')
                    }
                  )
                  if (index === -1) {
                    this.project.files[0].children[0].children.push(rfp)
                  } else {
                    this.project.files[0].children[0].children[index] = rfp
                  }
                }
              })

              resolve(true)
            })
        } catch (error) {
          reject(error)
        }
      })
    },

    getProject() {
      return new Promise((resolve, reject) => {
        db.collection('cpm_projects')
          .doc(this.$route.params.id)
          .get()
          .then(doc => {
            this.project = {
              id: doc.id,
              ...doc.data()
            }
            resolve()
          })
          .catch(reject)
      })
    }
  }
}
</script>

<style lang="scss">
.directory-item {
  width: 24px;
  height: auto;
}
</style>
